sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue !== '') {

        var ga = new GlideAjax('GetDetails');
        ga.addParam('sysparm_name', 'getUserDetails');
        ga.addParam('sys_id', newValue);
        ga.getXMLAnswer(function(response) {
            var userObj = JSON.parse(response);
            if (userObj.mobilePhone !== '')
                g_form.setValue('contact_number', userObj.mobilePhone, userObj.mobilePhone);
            else
                g_form.setValue('contact_number', userObj.businessPhone, userObj.businessPhone);

            var userLocation = userObj.location;

            var assignmentGroup = '';
            switch (userLocation) {
                case 'India':
                    assignmentGroup = 'Techsupport_India';
                    break;
                case 'United States':
                    assignmentGroup = 'Techsupport_USA';
                    break;
                case 'United Kingdom':
                    assignmentGroup = 'Techsupport_UK';
                    break;
                default:
                    assignmentGroup = '';
                    break;
            }
            if (assignmentGroup !== '') {
                ga = new GlideAjax('GetDetails');
                ga.addParam('sysparm_name', 'getGroupDetails');
                ga.addParam('groupName', assignmentGroup);
                ga.getXMLAnswer(function(response) {
                    var groupObj = JSON.parse(response);
                    g_form.setValue('assignment_group', groupObj.sysId, groupObj.name);
                });

            }
        });

    }

}
