sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    if (newValue === 'Issue') {
        g_form.setVisible('category', true);
        g_form.setVisible('approval', false);
        g_form.removeOption('state', '7');
        g_form.removeOption('state', '8');
        g_form.removeOption('state', '9');
        g_form.removeOption('state', '10');
        g_form.removeOption('state', '11');
        g_form.removeOption('state', '12');

        if (g_form.isNewRecord()) {
            g_form.setValue('approval', 'not requested', 'Not Yet Requested');
            var st = g_form.getValue('state');
            if (st === '') {
                var optionsIssue = [{
                        value: '1',
                        label: 'New'
                    },
                    {
                        value: '2',
                        label: 'In Progress'
                    },
                    {
                        value: '3',
                        label: 'On Hold'
                    },
                    {
                        value: '4',
                        label: 'Resolved'
                    },
                    {
                        value: '5',
                        label: 'Closed'
                    },
                    {
                        value: '6',
                        label: 'Cancelled'
                    }
                ];

                optionsIssue.forEach(function(option) {
                    g_form.addOption('state', option.value, option.label);
                });
            }
        }


    } else if (newValue === 'Request') {
        g_form.setVisible('approval', true);
        g_form.setVisible('category', false);
        g_form.removeOption('state', '1');
        g_form.removeOption('state', '2');
        g_form.removeOption('state', '3');
        g_form.removeOption('state', '4');
        g_form.removeOption('state', '5');
        g_form.removeOption('state', '6');

        if (g_form.isNewRecord()) {
            g_form.setValue('category', '', '-- None --');
            var stv = g_form.getValue('state');
            if (stv === '') {
                var optionsReq = [{
                        value: '7',
                        label: 'Open'
                    },
                    {
                        value: '8',
                        label: 'Pending'
                    },
                    {
                        value: '9',
                        label: 'Work in Progress'
                    },
                    {
                        value: '10',
                        label: 'Closed Complete'
                    },
                    {
                        value: '11',
                        label: 'Closed Incomplete'
                    },
                    {
                        value: '12',
                        label: 'Closed Skipped'
                    }
                ];

                optionsReq.forEach(function(option) {
                    g_form.addOption('state', option.value, option.label);
                });
            }
        }

    }

}
